import React from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Login from "./pages/Login";
import Register from "./pages/Register";
import EventList from "./components/EventList";
import EventDetail from "./components/EventDetail";
import StudentDashboard from "./components/StudentDashboard";
import AdminDashboard from "./components/AdminDashboard";
import Payment from "./pages/Payment";
import MyEvents from "./pages/MyEvents";
import CertificateTemplateUpload from "./pages/CertificateTemplateUpload";

// Add role-based routing using AuthContext (example version):
import { useAuth } from "./context/AuthContext";

function App() {
  const { user } = useAuth();
  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<EventList />} />
        <Route path="/events/:id" element={<EventDetail />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/payment/:eventId" element={<Payment />} />
        <Route path="/dashboard" element={user?.role === "student" ? <StudentDashboard /> : <AdminDashboard />} />
        <Route path="/my-events" element={<MyEvents />} />
        <Route path="/admin/events" element={<AdminDashboard />} />
        <Route path="/admin/certificate-template" element={<CertificateTemplateUpload />} />
        {/* ...additional pages */}
      </Routes>
    </BrowserRouter>
  );
}
export default App;
